def secret_code_generator():  
    letter_count = 6
    digit_count = 4
    str1 = ''.join((random.choice(string.ascii_letters) for x in range(letter_count)))  
    str1 += ''.join((random.choice(string.digits) for x in range(digit_count)))
    return str1

